import React, { useState } from "react";
import { Col, Row, Container } from "../components/Grid";
import { Input } from "../components/Form";
import { EmployeeList, EmployeeListItem } from "../components/EmployeeList";
import API from "../util/API";

function Directory() {

  const [searchState, setSearchState] = useState("");
  const [employees, setEmployees] = useState([]);
  // const employees = [{
  //   name: "Kyle Long1",
  //   thumbnail: "preview.png",
  //   phone: "(425)123-4567",
  //   email: "sampleemail@me.org",
  //   dob: "1/12/89"
  // }, {
  //   name: "Kyle Long2",
  //   thumbnail: "preview.png",
  //   phone: "(425)123-4567",
  //   email: "sampleemail@me.org",
  //   dob: "1/12/89"
  // }]

  API.getEmployees()
    .then(res => setEmployees(res.data.results))
    .catch(err => console.log(err))

  console.log(employees.slice(0, 10))

  // Write function that searches by employee lastname (via an API call) based on input's state

  const handleInputChange = event => {
    // Destructure the name and value properties off of event.target
    // Update the appropriate state
    const { value } = event.target;
    console.log(value);
    setSearchState(value);

    // Run function from line 10
  };

  return (
    <Container fluid>
      <Row>
        <Col size="md-12">
          <h1>Employee Directory</h1>
          <h4>Click on controls to filter by heading or use the search box to narrow your results</h4>
        </Col>
      </Row>
      <Row>
        <Col size="md-12">
          <form>
            <Input
              value={searchState}
              onChange={handleInputChange}
              name="search"
              placeholder="Search by last name"
            />
          </form>
        </Col>
      </Row>
      <Row>
        <Col size="md-12">
          {!employees.length ? (
            <h1 className="text-center">No Employees to Display</h1>
          ) : (
              <EmployeeList>
                {employees.slice(0, 10).map(employee => {
                  return (
                    <EmployeeListItem
                      key={employee.phone}
                      thumbnail={employee.picture.thumbnail}
                      name={employee.name.first}
                      phone={employee.phone}
                      email={employee.email}
                      dob={employee.dob.date}
                    />
                  );
                })}
              </EmployeeList>
            )}
        </Col>
      </Row>
    </Container>
  )
}

export default Directory;
